        setContentView(R.layout.activity_main);

        bookSearchBtn = findViewById(R.id.BookSearchBtn);
        bookSearchBtn.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                // ボタンをクリックした時の命令
            }
        });
