                // EditText
                String termString = bookSearchEditor.getText().toString();
                // Timer
                timer.cancel();
                //
                // Realm
                Realm realm = Realm.getDefaultInstance();
                try {
                    // 
                    realm.beginTransaction();
                    // 
                    SearchHistoryModel history = realm.createObject(SearchHistoryModel.class);
