                    realm.commitTransaction();
                } finally {
                    // Realm
                    realm.close();
                }
                //

                // Intent
                Intent intent = new Intent(MainActivity.this, ResultListActivity.class);
                // EditText"KeyValuePair"ResultListActivity
                intent.putExtra("terms", termString);
